# Write your MySQL query statement below
select visited_on, amount, average_amount from (
select visited_on, sum(amount) over(order by visited_on range between interval 6 day preceding and current row) as amount, round(avg(amount) over(order by visited_on range between interval 6 day preceding and current row), 2) as average_amount, count(amount) over(order by visited_on range between interval 6 day preceding and current row) as cnt from (select visited_on, sum(amount) as amount from Customer group by visited_on) b) a where cnt >= 7;